import { render, screen } from '@testing-library/react'
import { userEvent } from '@testing-library/user-event'
import { describe, it, expect, vi } from 'vitest'
import { {{COMPONENT_NAME}} } from '@/components/{{COMPONENT_PATH}}'

describe('{{COMPONENT_NAME}}', () => {
  it('should render correctly', () => {
    render(<{{COMPONENT_NAME}} />)

    // Arrange & Act
    const element = screen.getByRole('{{ROLE}}')

    // Assert
    expect(element).toBeInTheDocument()
  })

  it('should handle user interaction', async () => {
    const user = userEvent.setup()
    const mockOnClick = vi.fn()

    render(<{{COMPONENT_NAME}} onClick={mockOnClick} />)

    // Act
    await user.click(screen.getByRole('button'))

    // Assert
    expect(mockOnClick).toHaveBeenCalledTimes(1)
  })

  it('should display correct text content', () => {
    const testText = 'Test Content'

    render(<{{COMPONENT_NAME}} text={testText} />)

    expect(screen.getByText(testText)).toBeInTheDocument()
  })

  it('should apply correct classes', () => {
    render(<{{COMPONENT_NAME}} className="custom-class" />)

    const element = screen.getByRole('{{ROLE}}')
    expect(element).toHaveClass('custom-class')
  })
})

