import { render, screen } from '@testing-library/react'
import { describe, it, expect } from 'vitest'
import { {{COMPONENT_NAME}}, {{COMPONENT_NAME}}Props } from '@/components/{{COMPONENT_PATH}}'

describe('{{COMPONENT_NAME}}', () => {
  const defaultProps: {{COMPONENT_NAME}}Props = {
    // Add default props here
  }

  it('should render with default props', () => {
    render(<{{COMPONENT_NAME}} {...defaultProps} />)
    expect(screen.getByRole('{{ROLE}}')).toBeInTheDocument()
  })

  it('should render with custom props', () => {
    const customProps = {
      ...defaultProps,
      variant: 'primary'
    }

    render(<{{COMPONENT_NAME}} {...customProps} />)
    expect(screen.getByRole('{{ROLE}}')).toHaveAttribute('data-variant', 'primary')
  })
})

