import { renderHook } from '@testing-library/react'
import { describe, it, expect } from 'vitest'
import { {{HOOK_NAME}} } from '@/hooks/{{HOOK_PATH}}'
import { {{CONTEXT_NAME}}Provider } from '@/contexts/{{CONTEXT_PATH}}'
import { ReactNode } from 'react'

const wrapper = ({ children }: { children: ReactNode }) => (
  <{{CONTEXT_NAME}}Provider>{children}</{{CONTEXT_NAME}}Provider>
)

describe('{{HOOK_NAME}}', () => {
  it('should access context values', () => {
    const { result } = renderHook(() => {{HOOK_NAME}}(), { wrapper })

    expect(result.current.contextValue).toBeDefined()
  })
})
